function Graphic(a,c,b){this.canvas=a;this.context=a.getContext("2d");this.linesX=c-2;this.linesY=b-2;this.tileX=this.canvas.width/this.linesX;this.tileY=this.canvas.height/this.linesY}Graphic.prototype.render=function(e,d,c){this.context.clearRect(0,0,this.canvas.width,this.canvas.height);for(var a=0;a<e.grid.length-1;a++){for(var b=0;b<e.grid[a].length-1;b++){this.drawBox(b,a,e.grid[a+1][b+1].cor);this.drawText(b,a,e.grid[a+1][b+1].text)}}this.drawBox(d.x-1,d.y-1,"rgba(255,0,0,0.5)");this.drawBox(c.x-1,c.y-1,"rgba(0,0,255,0.5)");this.drawGridLines()};Graphic.prototype.drawGridLines=function(){this.context.beginPath();var a;for(a=0;a<this.linesY;a++){this.context.moveTo(0,a*this.tileY-0.5);this.context.lineTo(this.canvas.width,a*this.tileY-0.5)}for(var b=0;b<this.linesX;b++){this.context.moveTo(b*this.tileX-0.5,0);this.context.lineTo(b*this.tileX-0.5,this.canvas.height)}this.context.save();this.context.strokeStyle="#555";this.context.stroke();this.context.beginPath();this.context.restore()};Graphic.prototype.drawText=function(a,c,b){this.context.save();this.context.font="10pt Georgia";this.context.textAlign="center";this.context.textBaseline="middle";this.context.fillText(b,this.tileX*a+this.tileX/2,this.tileY*c+this.tileY/2,this.tileX);this.context.restore()};Graphic.prototype.drawBox=function(a,c,b){this.context.save();this.context.fillStyle=b;this.context.fillRect(this.tileX*a,this.tileY*c,this.tileX,this.tileY);this.context.restore()};